def volume(side_length):
    return side_length ** 3

def surface_area(side_length):
    return 6 * side_length ** 2

def diagonal_length(side_length):
    return (3 * side_length ** 2) ** 0.5

def face_diagonal_length(side_length):
    return (2 * side_length ** 2) ** 0.5

def space_diagonal_length(side_length):
    return (3 * side_length ** 2) ** 0.5

def inner_surface_area(side_length):
    return 4 * side_length ** 2

def edge_length(side_length):
    return 12 * side_length

def total_edge_length(side_length):
    return 12 * side_length

def inner_volume(side_length):
    return side_length ** 3

def inner_diagonal_length(side_length):
    return (2 * side_length ** 2) ** 0.5
