    "import h5py\n",
    "import numpy as np\n",
    "\n",
    "\n",
    "x = np.zeros(224)\n",
    "i=0\n",
    "file_in = open(\"xxx_Y_COORD.txt\", \"r\")\n",
    "for y in file_in.read().split('\\n'):\n",
    "    #if y.isdigit():\n",
    "    x[i]=float(y)\n",
    "    i=i+1\n",
    "    #print(i)\n",
    "\n",
    "print(x)\n",
    "    #using single precision since all getF functions need single precision\n",
    "np.save('xxx_ygrid', x.astype(np.float32))\n"
